import React, { useEffect, useState } from "react";

const Modal = ({ isOpen, onClose, onConfirm }) => {
  const [isAnimating, setIsAnimating] = useState(false);

  const handleClose = () => {
    setIsAnimating(true); // Start closing animation
    setTimeout(() => {
      onClose(); // Close the modal after the animation duration
    }, 300); // Match this duration with the CSS animation duration
  };

  useEffect(() => {
    if (isOpen) {
      setIsAnimating(false); // Reset animation when modal opens
    }
  }, [isOpen]);

  if (!isOpen && !isAnimating) return null; // Don't render if not open and not animating

  return (
    <div
      className={`fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-50 ${
        isAnimating ? "opacity-0" : "opacity-100"
      }`}
    >
      <div
        className={`bg-white rounded-lg shadow-lg p-6 transition-transform duration-300 ${
          isAnimating ? "scale-75" : "scale-100"
        }`}
      >
        <h2 className="text-lg font-bold">Confirm Logout</h2>
        <p>Are you sure you want to log out?</p>
        <div className="mt-4 flex justify-end">
          <button
            className="bg-red-500 text-white rounded px-4 py-2 mr-2"
            onClick={() => {
              onConfirm();
              handleClose(); // Trigger the close animation on confirm
            }}
          >
            Yes, Logout
          </button>
          <button
            className="bg-gray-300 rounded px-4 py-2"
            onClick={handleClose}
          >
            Cancel
          </button>
        </div>
      </div>
    </div>
  );
};

export default Modal;
