import React from "react";
import { Routes, Route, Navigate, useLocation } from "react-router-dom";
import "./App.css";
import { Helmet } from "react-helmet";
import Header from "./navbar/Header";
import GenerateImage from "./navbar/GenerateImage";
import RemoveBackground from "./navbar/RemoveBg";
import SocialMedia from "./navbar/SocialMedia";
import Tutorial from "./pages/Tutorial";
import VideoDownloader from "./components/VideoDownloader";
import SignIn from "./pages/SignIn";
import SignUp from "./pages/SignUp"; // Import SignUp
import Footer from "./navbar/Footer";
import About from "./pages/About";
import Contact from "./pages/Contact";
import Saran from "./pages/Saran";

function App() {
  const location = useLocation();
  const isAuthPage =
    location.pathname === "/SignIn" || location.pathname === "/SignUp"; // Check for both sign-in and sign-up pages

  return (
    <div className="App min-h-screen flex flex-col dark:bg-gray-600">
      <Helmet>
        <title>VidSwift</title>
      </Helmet>
      {/* Display Header only if it's not an auth page */}
      {!isAuthPage && <Header />}
      <div className="flex-grow">
        <Routes>
          <Route path="/" element={<Navigate to="/dashboard" />} />
          <Route path="/dashboard" element={<VideoDownloader />} />
          <Route path="/SignIn" element={<SignIn />} /> {/* SignIn route */}
          <Route path="/SignUp" element={<SignUp />} /> {/* SignUp route */}
          <Route path="/tutorial" element={<Tutorial />} />
          <Route path="/generate-image" element={<GenerateImage />} />
          <Route path="/remove-bg" element={<RemoveBackground />} />
          <Route path="/social-media" element={<SocialMedia />} />
          <Route path="/about" element={<About />} />
          <Route path="/contact" element={<Contact />} />
          <Route path="/saran" element={<Saran />} />
        </Routes>
      </div>
      {/* Display Footer only if it's not an auth page */}
      {!isAuthPage && <Footer />}
    </div>
  );
}

export default App;
