import React, { useEffect, useState } from "react";

const Modal = ({ isOpen, onClose, onConfirm }) => {
  const [isAnimating, setIsAnimating] = useState(false);

  useEffect(() => {
    if (!isOpen) {
      setIsAnimating(true); // Start animation when closing
      const timer = setTimeout(() => {
        setIsAnimating(false); // Reset animation state after animation duration
      }, 300); // Duration of the animation
      return () => clearTimeout(timer); // Cleanup timer on component unmount or re-render
    }
  }, [isOpen]);

  if (!isOpen && !isAnimating) return null; // Don't render anything if not open and not animating

  return (
    <div className={`fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-50 ${isAnimating ? 'opacity-0' : 'opacity-100'}`}>
      <div
        className={`bg-white rounded-lg shadow-lg p-6 transition-transform duration-300 ${isAnimating ? 'scale-75' : 'scale-100'}`}
      >
        <h2 className="text-lg font-bold">Confirm Logout</h2>
        <p>Are you sure you want to log out?</p>
        <div className="mt-4 flex justify-end">
          <button
            className="bg-red-500 text-white rounded px-4 py-2 mr-2"
            onClick={() => {
              onConfirm();
              onClose(); // Close the modal immediately after confirming
            }}
          >
            Yes, Logout
          </button>
          <button className="bg-gray-300 rounded px-4 py-2" onClick={onClose}>
            Cancel
          </button>
        </div>
      </div>
    </div>
  );
};

export default Modal;
